'use strict';

const _ = require('lodash');

const camelCaseTransform = (name, details) => _.camelCase(name);
const pascalCaseTransform = (name, details) => _.upperFirst(_.camelCase(name));

const library = {
    modelName: [
        { name: 'camelCase', transformer: camelCaseTransform },
        { name: 'pascalCase', transformer: pascalCaseTransform }
    ],
    operationName: [
        { name: 'camelCase', transformer: camelCaseTransform },
        { name: 'pascalCase', transformer: pascalCaseTransform }
    ],
    propertyName: [
        { name: 'camelCase', transformer: camelCaseTransform },
        { name: 'pascalCase', transformer: pascalCaseTransform }
    ],
    serviceName: [
        { name: 'camelCase', transformer: camelCaseTransform },
        { name: 'pascalCase', transformer: pascalCaseTransform }
    ],
};

module.exports = library;
