'use strict';

const library = require('./library');

function getTypeStruct(type) {
    switch (type) {
        case 'modelName': return library.modelName;
        case 'propertyName': return library.propertyName;
        case 'operationName': return library.operationName;
        case 'serviceName': return library.serviceName;
        default: throw new Error(`Cannot register transformer type ${type}.`);
    }
}

module.exports = function(type, name, transformer) {
    let typeStruct = getTypeStruct(type);
    typeStruct.push({ name: name, transformer: transformer });
}
